html, body {
  font-size: 18px;
  background: #0e4f91;
  font-family: "Barlow Semi Condensed", sans-serif;
  height: 100%;
}

.menu-icon {
  position: fixed;
  right: 36px;
  top: 36px;
  z-index: 11;
  padding: 20px;
}
.menu-icon .fa {
  font-size: 2rem;
  color: #0e4f91;
  text-decoration: none;
}

#menu {
  background: #0e4f91;
  position: fixed;
  width: 100%;
  top: 0;
  bottom: 0;
  transform: translate3d(-100%, 0, 0);
  z-index: 10;
  display: flex;
  text-decoration: none;
  align-items: center;
  justify-content: center;
  opacity: 1;
  transition: all 0.5s ease-in-out;
}
#menu nav {
  width: 400px;
  text-align: center;
}
#menu nav a {
  display: block;
  font-size: 3rem;
  padding: 1rem;
  color: white;
  text-decoration: none;
}
#menu nav a:hover {
  color: gray;
}

.menue-active .menu-icon .fa {
  color: white;
}
.menue-active #menu {
  width: 100%;
  top: 0;
  bottom: 0;
  transform: translate3d(200px, 0, 0);
}

.dropbtn {
  background-color: #0e4f91;
  color: white;
  padding: 20px;
  font-size: 16px;
  border: none;
  cursor: pointer;
}

.dropdown {
  position: relative;
  display: inline-block;
}

.dropdown-content {
  display: none;
  position: absolute;
  right: 0;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
  z-index: 1;
}

.dropdown-content a {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;
}

.dropdown-content a:hover {
  background-color: #42f4d7;
}

.dropdown:hover .dropdown-content {
  display: block;
}

.dropdown:hover .dropbtn {
  background-color: #42f4d7;
}

.primary-btn {
  background: #0e4f91;
  -webkit-border-radius: 30px;
  -moz-border-radius: 30px;
  border-radius: 30px;
  text-align: center;
  color: white;
  padding: 15px 30px;
  display: inline-block;
  text-transform: uppercase;
  letter-spacing: 3px;
  text-decoration: none;
  transition: all 0.3s ease-in-out;
}
.primary-btn:hover {
  background-color: #42f4d7;
  color: #0e4f91;
  font-weight: bold;
}

.resume-btn {
  border: 4px solid #0e4f91;
  -webkit-border-radius: 30px;
  -moz-border-radius: 30px;
  border-radius: 30px;
  text-align: center;
  color: #0e4f91;
  padding: 12px 27px;
  display: inline-block;
  text-transform: uppercase;
  letter-spacing: 3px;
  margin-left: 15px;
  font-weight: bold;
  text-decoration: none;
  transition: all 0.3s ease-in-out;
}
.resume-btn:hover {
  border: 0;
  background-color: #42f4d7;
  color: #0e4f91;
  font-weight: bold;
  padding: 15px 30px;
}

.scroll {
  height: 100vh;
  overflow-y: scroll;
  overflow-x: hidden;
  padding: 56px;
}

@media (max-width: 768px) {
  body {
    background-color: white;
  }
  body .logo {
    position: absolute;
    top: 0;
    left: -2%;
  }
}

